import logo from './logo.svg';
import './App.css';
import React, {useReducer, useState} from 'react';

let initialFile ={photo: null};

function reducer(state, action){
  switch (action.type){
    case "photo1":
      state.photo = URL.createObjectURL(action.event.target.files[0])        
      return state;
    case "photo2":
      state.photo= URL.createObjectURL(action.event.target.files[0])       
      return state;    

    default:
      throw new Error();
  }
}



function App() {
  //const [state, dispatch] = useReducer(reducer, initialFile);

  const [photo, setPhoto] = useState(null);

  console.log(`${typeof(state)} ${setState}`);

  function OnPhoto1Selected(event) {
    setState(URL.createObjectURL(event.target.files[0]));

  }

  // const onSelected = (i, e)=>{
  //   dispatch({type:i, event:e})
  // }
  return (
    <div className="App">

      <div class="row">
        <div class="col-sm-4">
          <h2>Photo 1</h2>        
        </div>
        <div class="col-sm-4">
          <h2> Photo 2 </h2>
        </div>
        <div class="col-sm-4">
          <h2>Morphed Photo</h2>
        </div>
      </div>


      <div class="row">
        <div class="col-sm-4">          
          {/* <input type="file" onChange={onSelected("photo1", Event)}/>       */}
          <input type="file" onChange={OnPhoto1Selected}/>
          <img src={photo} />
        </div>



      </div>


    </div>
  );
}



export default App;
