import logo from './logo.svg';
import './App.css';
import React, {useState} from 'react';



function App() {
  const [file, setFile] = useState();

  function onFileSelected(event){
    setFile(URL.createObjectURL(event.target.files[event.target.files.length-1]))
  }

  return (
    <div className="App">

      <div class="row">
        <div class="col-sm-4">
          <h2>Photo 1</h2>        
        </div>
        <div class="col-sm-4">
          <h2> Photo 2 </h2>
        </div>
        <div class="col-sm-4">
          <h2>Morphed Photo</h2>
        </div>
      </div>


      <div class="row">
        <div class="col-sm-4">
          <img src={file}/>
          <input type="file" onChange={onFileSelected}/>      
        </div>
        <div class="col-sm-4">
          <img src={file}/>
          <input type="file" onChange={onFileSelected}/>
        </div>
        <div class="col-sm-4">
        {/* to insert download link */}
        </div>   

      </div>


    </div>
  );
}



export default App;
