import logo from './logo.svg';
import './App.css';
import React, {useReducer} from 'react';

let initialFile ={photo: null};

function reducer(state, action){
  switch (action.type){
    case "photo1":
      state.photo = URL.createObjectURL(action.event.target.files[0])        
      return state;
    case "photo2":
      state.photo= URL.createObjectURL(action.event.target.files[0])       
      return state;    

    default:
      throw new Error();
  }
}

function App() {
  const [state, dispatch] = useReducer(reducer, initialFile);

  const onSelected = (i, e)=>{
    dispatch({type:i, event:e})
  }
  return (
    <div className="App">

      <div class="row">
        <div class="col-sm-4">
          <h2>Photo 1</h2>        
        </div>
        <div class="col-sm-4">
          <h2> Photo 2 </h2>
        </div>
        <div class="col-sm-4">
          <h2>Morphed Photo</h2>
        </div>
      </div>


      <div class="row">
        <div class="col-sm-4">
          <img src={state.photo}/>
          <input type="file" onChange={onSelected("photo1", Event)}/>      
        </div>
        {/* <div class="col-sm-4">
          <img src={file}/>
          <input type="file" onChange={onSelected("photo2", Event)}/>
        </div> */}
        {/* <div class="col-sm-4"> */}
        {/* to insert download link */}
        {/* </div>    */}

      </div>


    </div>
  );
}



export default App;
